using System.Text;

namespace DemoLauncher;

/// <summary>
/// Provides the HTML GUI for demo launchers (intended to be displayed in a web view)
/// </summary>
public class HtmlMenu
{
    private readonly StringBuilder Content = new();
    private readonly string VersionString;

    public HtmlMenu(string versionString)
    {
        VersionString = versionString;
    }

    public void Add(string title, string description)
    {
        Content.AppendLine($"<div class='card' onclick=\"postIt('{title}')\">");
        Content.AppendLine($"  <div class='card-title'>{title}</div>");
        Content.AppendLine($"  <div>{description}</div>");
        Content.AppendLine($"</div>");
    }

    public override string ToString()
    {
        return @"
            <html>
            <head>
                <style>{{STYLE}}</style>
                <script>
                    function postIt(title) {
                        console.log('posting: ' + title);
                        window.chrome.webview.postMessage(title);
                    }
                </script>
            </head>
            <body>
                <div class='banner'>
                    <img class='banner-icon'
                        src='data:image/png;base64,' />
                    <div>
                        <div class='banner-title'>{{VERSION}}</div>
                        <div class='banner-subtitle'>Windows Forms Demo</div>
                    </div>
                </div>
                <section class='cards'>{{CONTENT}}</section>
            </body>
            </html>"
            .Replace("{{STYLE}}", CSS())
            .Replace("{{VERSION}}", VersionString)
            .Replace("{{CONTENT}}", Content.ToString());
    }

