            }

            .banner-title {
                margin-top: .15em;
                font-size: 2.5em;
                line-height: 100%;
            }

            .banner-subtitle {
                font-size: 1.5em;
                font-weight: 300;
                line-height: 100%;
                color: rgba(255, 255, 255, .5);
            }

            .card {
                margin: .5em;
                margin-bottom: 1em;
                background-color: rgba(154, 73, 147, .3);
                padding: 1em;
                border-radius: .75em;
                color: rgba(255, 255, 255, .8);
                transition: 0.1s;
            }

            .card:hover {
                background-color: rgba(154, 73, 147, 1.0);
                color: white;
                transition: 0.1s;
                cursor: pointer;
            }

            .card-title {
                font-size: 1.5em;
            }
        ";
    }
}
